from __future__ import annotations

from genshi.core import COMMENT
from genshi.core import DOCTYPE
from genshi.core import END
from genshi.core import END_CDATA
from genshi.core import END_NS
from genshi.core import PI
from genshi.core import QName
from genshi.core import START
from genshi.core import START_CDATA
from genshi.core import START_NS
from genshi.core import TEXT
from genshi.core import XML_NAMESPACE

from . import base
from ..constants import namespaces
from ..constants import voidElements


class TreeWalker(base.TreeWalker):
    def __iter__(self):
        # Buffer the events so we can pass in the following one
        previous = None
        for event in self.tree:
            if previous is not None:
                yield from self.tokens(previous, event)
            previous = event

        # Don't forget the final event!
        if previous is not None:
            yield from self.tokens(previous, None)

    def tokens(self, event, next):
        kind, data, _ = event
        if kind == START:
            tag, attribs = data
            name = tag.localname
            namespace = tag.namespace
            converted_attribs = {}
            for k, v in attribs:
                if isinstance(k, QName):
                    converted_attribs[(k.namespace, k.localname)] = v
                else:
                    converted_attribs[(None, k)] = v

            if namespace == namespaces['html'] and name in voidElements:
                yield from self.emptyTag(
                    namespace, name, converted_attribs,
                    not next or next[0] != END or
                    next[1] != tag,
                )
            else:
                yield self.startTag(namespace, name, converted_attribs)

        elif kind == END:
            name = data.localname
            namespace = data.namespace
            if namespace != namespaces['html'] or name not in voidElements:
                yield self.endTag(namespace, name)

        elif kind == COMMENT:
            yield self.comment(data)

        elif kind == TEXT:
            yield from self.text(data)

        elif kind == DOCTYPE:
            yield self.doctype(*data)

        elif kind in (
            XML_NAMESPACE, DOCTYPE, START_NS, END_NS,
            START_CDATA, END_CDATA, PI,
        ):
            pass

        else:
            yield self.unknown(kind)
